// NOTE: Do not change event name
function E_ICON_TIME_1615618745280()
{
    if (($S.FirstScreenView.toUpperCase() == ""))
    {
        $F.setFacts("E_ICON_TIME_1615618745280");
    }
}

// NOTE: Do not change event name
function E_ICON_EVENT_1615618648559()
{
    // check if new stone found
    var stone = $S.getCustomVar("SSV_1094"); // Icon Event Found
    if (stone !== "")
    {
        // blank found flag event
        $S.setCustomVar("SSV_1094", "");
        $S.setCustomVar("SSV_1093", stone); // Icon Event

        // set icon
        var iconObj = $S.getCustomJSObj("icons") || { msgs: [] };
        iconObj.msgs.push(stone);
        $S.setCustomJSObj("icons", iconObj);
        // set session icon object
        $S.setCustomVar("SSV_1092", iconObj.msgs.join(" ")); // Icon list

        // check if previous event
        var lastStone, lastStoneTime;
        if ($F.getEventCount("E_ICON_EVENT_1615618648559") === 0)
        {
            // first event in icon list
            lastStone = "empty";
            lastStoneTime = 0;
        }
        else
        {
            lastStone = $F.getLastEventValue("E_ICON_EVENT_1615618648559");
            lastStoneTime = Math.round(($S.LastHitEpoch - $F.getLastHitTime("E_ICON_EVENT_1615618648559"))/1000);
            // separate event to get time as numeric value
            $F.setFacts("E_ICON_TIME_1615618745280", lastStoneTime, { "DIM_ICON_EVENT_1615619521345": lastStone, "DIM_ICON_EVENT_TIME_1615619612227": lastStoneTime });
        }

        // set event with value=stone, dim=lastStone+timing
        $F.setFacts("E_ICON_EVENT_1615618648559", stone, { "DIM_ICON_EVENT_1615619521345": lastStone, "DIM_ICON_EVENT_TIME_1615619612227": lastStoneTime });
    }
}

